mapper.select:
select oi.id, oi.product_id, p.name as product_name, '${url}'|| (select pi.name from product_image pi where pi.product_id=p.id and rownum=1) as image,
oi.quantity, oi.total_price, oi.delivery_at, oi.status, oi.review_writable
from order_item oi join product p on oi.product_id=p.id where oi.order_id=#{orderId}
